from binascii import unhexlify
from Crypto.Cipher import AES
import base64



"""
    Ici la clé est une chaîne de 16 octets. On peut donc brute force le décodage.
"""

K =  'c39846ea70fdd396772bdecc263c8605'      #16 octets
IV = 'af11a260875629b708243ecaebb3dc6c'     #16 octets
ciphertext = ''

pass_IV = unhexlify(IV)
pass_K = unhexlify(K)
decode = AES.new(pass_K, AES.MODE_CBC, pass_IV)
plain = decode.decrypt(base64.b64decode(ciphertext))
print(plain.decode())